mapper.select:
SELECT *
FROM
(
	SELECT ROWNUM AS NUM , INNER_SQL.* 
	FROM (


			AND TO_CHAR(TA.CREATE_DATE,'YYYY-MM-DD HH24:MI:SS') >= #{stime}


			AND TO_CHAR(TA.CREATE_DATE,'YYYY-MM-DD HH24:MI:SS') <= #{etime}

	) INNER_SQL 
	WHERE 1=1

	AND ROWNUM >= ((#{page}-1)*#{count}+1)
	AND ROWNUM < (#{page})*#{count}

)
